from django.urls import path, re_path
from django.http import HttpResponse, HttpRequest, JsonResponse
import pylims, json, os
from pydantic import BaseModel, EmailStr, constr, ValidationError, validator
import psycopg
from psycopg.rows import dict_row
import bcrypt

current_directory = os.path.dirname(os.path.abspath(__file__))
self = os.path.basename(current_directory)

def getOrg(params):
    response={}
    response['data']={}
    mods = json.loads(pylims.get_setup_options())
    mod=mods['options']['organization'][self];
    # print('mod',params['modinfo'])
    if not 'userid' in params:
        response['error']='no userid'
        response['status']='failed'
        return response
    # print('getOrg userid',int(params['userid']))
    if 'departments' in mods['setup'] and not mods['setup']['departments']=='disabled':
        response['data']['departments']=mods['setup']['departments']
    conn = psycopg.connect(dbname=pylims.dbname, user=pylims.dbuser, password=pylims.dbpass, host=pylims.dbhost, port=pylims.dbport, row_factory=dict_row)
    cursor = conn.cursor()

    cursor.execute("SELECT * FROM user_organization JOIN organizations ON oid=user_organization.org WHERE user_organization.user = %s::integer LIMIT 1;", (int(params['userid']),))

    results = cursor.fetchone()
    # print('results',results)
    if results==None:
        cursor.execute("""SELECT 
            column_name
        FROM information_schema.columns
        WHERE table_name = 'user_organization'
        UNION
        SELECT 
            column_name
        FROM information_schema.columns
        WHERE table_name = 'organizations';""")
        results = cursor.fetchall()
        
        for result in results:
            if result['column_name']=='oid' or result['column_name']=='uoid':
                continue
            response['data'][result['column_name']]=''
        response['data']['organization_name']='none'
        response['data']['description']='you have not been added to an organization yet.'
        response['data']['image']='data:image/svg+xml;base64,'
        response['status']='no org'
        return response
    else:
        response['data'].update(results)
    cursor.close()
    conn.close()
    
    
    
    if not 'error' in response:
        response['status']='success'
    return response

urlpatterns=[
    path('create_account/', getOrg, name="pylims_create_account"),
    ]
    
